import { motion } from 'framer-motion';
import { Rocket, Clock, TrendingUp } from 'lucide-react';
import { Card } from '@/components/ui/card';
import { Button } from '@/components/ui/button';
import { Progress } from '@/components/ui/progress';
import type { Boost } from '@shared/schema';

interface BoostCardProps {
  boosts: Boost[];
  onActivateBoost?: (boostType: string) => Promise<void>;
}

const boostTypes = [
  {
    type: '2x_speed',
    name: '2x Mining Speed',
    multiplier: 2,
    duration: 3600,
    cost: 100,
    energyCost: 20,
    description: 'Double your mining speed for 1 hour',
    icon: Rocket,
    color: 'from-blue-500 to-cyan-500',
  },
  {
    type: '3x_speed',
    name: '3x Mining Speed',
    multiplier: 3,
    duration: 1800,
    cost: 200,
    energyCost: 30,
    description: 'Triple your mining speed for 30 minutes',
    icon: TrendingUp,
    color: 'from-purple-500 to-pink-500',
  },
  {
    type: '5x_speed',
    name: '5x Mining Speed',
    multiplier: 5,
    duration: 900,
    cost: 500,
    energyCost: 50,
    description: '5x mining speed for 15 minutes',
    icon: Rocket,
    color: 'from-amber-500 to-orange-500',
  },
];

export default function BoostCard({ boosts, onActivateBoost }: BoostCardProps) {
  const activeBoosts = boosts.filter(b => b.isActive);

  const getTimeRemaining = (expiresAt: Date) => {
    const now = new Date();
    const diff = new Date(expiresAt).getTime() - now.getTime();
    if (diff <= 0) return '0s';

    const minutes = Math.floor(diff / 60000);
    const seconds = Math.floor((diff % 60000) / 1000);
    return minutes > 0 ? `${minutes}m ${seconds}s` : `${seconds}s`;
  };

  const getProgress = (startedAt: Date, expiresAt: Date) => {
    const now = new Date();
    const start = new Date(startedAt).getTime();
    const end = new Date(expiresAt).getTime();
    const current = now.getTime();

    if (current >= end) return 0;
    if (current <= start) return 100;

    return ((end - current) / (end - start)) * 100;
  };

  return (
    <div className="space-y-4">
      {activeBoosts.length > 0 && (
        <Card className="p-4 bg-gradient-to-br from-primary/10 to-primary/5" data-testid="card-active-boosts">
          <h3 className="font-bold text-lg mb-3 flex items-center gap-2">
            <Rocket className="w-5 h-5 text-primary" />
            Active Boosts
          </h3>
          <div className="space-y-3">
            {activeBoosts.map((boost) => {
              const boostInfo = boostTypes.find(b => b.type === boost.boostType);
              if (!boostInfo) return null;

              const progress = getProgress(boost.startedAt, boost.expiresAt);
              const timeRemaining = getTimeRemaining(boost.expiresAt);

              return (
                <motion.div
                  key={boost.id}
                  initial={{ scale: 0.9, opacity: 0 }}
                  animate={{ scale: 1, opacity: 1 }}
                  className={`p-3 rounded-lg bg-gradient-to-r ${boostInfo.color} text-white`}
                  data-testid={`boost-active-${boost.boostType}`}
                >
                  <div className="flex items-center justify-between mb-2">
                    <div className="flex items-center gap-2">
                      <boostInfo.icon className="w-5 h-5" />
                      <span className="font-semibold">{boost.multiplier}x Speed</span>
                    </div>
                    <div className="flex items-center gap-1 text-sm">
                      <Clock className="w-4 h-4" />
                      {timeRemaining}
                    </div>
                  </div>
                  <Progress value={progress} className="h-2 bg-white/30" />
                </motion.div>
              );
            })}
          </div>
        </Card>
      )}

      <Card className="p-6" data-testid="card-boost-shop">
        <h3 className="font-bold text-xl mb-4 font-['Poppins']">Mining Boosts</h3>
        <p className="text-sm text-muted-foreground mb-4">
          Activate boosts to increase your mining speed temporarily
        </p>

        <div className="grid gap-3">
          {boostTypes.map((boost) => (
            <Card 
              key={boost.type} 
              className={`p-4 bg-gradient-to-br ${boost.color} text-white hover:shadow-lg transition-shadow`}
              data-testid={`boost-option-${boost.type}`}
            >
              <div className="flex items-start justify-between mb-3">
                <div className="flex items-start gap-3">
                  <div className="w-12 h-12 rounded-full bg-white/20 flex items-center justify-center">
                    <boost.icon className="w-6 h-6" />
                  </div>
                  <div>
                    <h4 className="font-bold text-lg">{boost.name}</h4>
                    <p className="text-sm opacity-90">{boost.description}</p>
                  </div>
                </div>
              </div>

              <div className="flex items-center justify-between">
                <div className="text-sm space-y-1">
                  <div className="flex items-center gap-1">
                    <Clock className="w-4 h-4" />
                    Duration: {boost.duration / 60} min
                  </div>
                  <div className="text-xs opacity-80">
                    Cost: {boost.cost} CASET + {boost.energyCost} Energy
                  </div>
                </div>
                <Button
                  onClick={() => onActivateBoost?.(boost.type)}
                  variant="secondary"
                  size="sm"
                  className="font-semibold"
                  data-testid={`button-activate-${boost.type}`}
                >
                  Activate
                </Button>
              </div>
            </Card>
          ))}
        </div>
      </Card>
    </div>
  );
}

client/src/components/CoinDisplay.tsx.content:
import { useEffect, useState } from 'react';
import logoImage from '@assets/generated_images/PingCaset_crypto_logo_icon_e52ce570.png';
import { motion, useSpring, useTransform } from 'framer-motion';

interface CoinDisplayProps {
  amount: number;
  label?: string;
  size?: 'sm' | 'md' | 'lg' | 'xl';
  showAnimation?: boolean;
}

export default function CoinDisplay({ amount, label = 'Balance', size = 'lg', showAnimation = false }: CoinDisplayProps) {
  const [displayAmount, setDisplayAmount] = useState(amount);

  const spring = useSpring(0, { stiffness: 300, damping: 30 });
  const animatedAmount = useTransform(spring, (current) => Math.floor(current).toLocaleString('en-US', { minimumFractionDigits: 2, maximumFractionDigits: 2 }));

  useEffect(() => {
    spring.set(amount);
  }, [amount, spring]);

  useEffect(() => {
    if (showAnimation) {
      const duration = 1000;
      const steps = 30;
      const increment = (amount - displayAmount) / steps;
      let currentStep = 0;

      const timer = setInterval(() => {
        currentStep++;
        if (currentStep >= steps) {
          setDisplayAmount(amount);
          clearInterval(timer);
        } else {
          setDisplayAmount(prev => prev + increment);
        }
      }, duration / steps);

      return () => clearInterval(timer);
    } else {
      setDisplayAmount(amount);
    }
  }, [amount, showAnimation]);

  const sizeClasses = {
    sm: 'text-lg',
    md: 'text-2xl',
    lg: 'text-4xl',
    xl: 'text-5xl md:text-6xl'
  };

  const iconSizes = {
    sm: 'w-6 h-6',
    md: 'w-8 h-8',
    lg: 'w-12 h-12',
    xl: 'w-16 h-16'
  };

  const itemVariants = {
    hidden: { opacity: 0 },
    visible: { opacity: 1, transition: { duration: 0.3 } },
  };

  return (
    <motion.div 
      className="text-center relative" 
      data-testid="coin-display"
      variants={itemVariants}
      initial="hidden"
      animate="visible"
    >
      <p className="text-sm text-muted-foreground mb-2" data-testid="text-coin-label">{label}</p>
      <div className="flex items-center justify-center gap-3 relative">
        <motion.img 
          src={logoImage} 
          alt="Caset Coin" 
          className={`${iconSizes[size]}`}
          animate={{ 
            rotate: [0, 5, -5, 0],
            y: [0, -5, 0]
          }}
          transition={{
            duration: 3,
            repeat: Infinity,
            ease: "easeInOut"
          }}
        />

        <motion.span 
          className={`${sizeClasses[size]} font-bold font-['Poppins'] tabular-nums`}
          data-testid="text-coin-amount"
        >
          {animatedAmount}
        </motion.span>
      </div>
      <p className="text-xs text-muted-foreground mt-1">CASET</p>
    </motion.div>
  );
}

client/src/components/ui/carousel.tsx.content:
import * as React from "react"
import useEmblaCarousel, {
  type UseEmblaCarouselType,
} from "embla-carousel-react"
import { ArrowLeft, ArrowRight } from "lucide-react"

import { cn } from "@/lib/utils"
import { Button } from "@/components/ui/button"

type CarouselApi = UseEmblaCarouselType[1]
type UseCarouselParameters = Parameters<typeof useEmblaCarousel>
type CarouselOptions = UseCarouselParameters[0]
type CarouselPlugin = UseCarouselParameters[1]

type CarouselProps = {
  opts?: CarouselOptions
  plugins?: CarouselPlugin
  orientation?: "horizontal" | "vertical"
  setApi?: (api: CarouselApi) => void
}

type CarouselContextProps = {
  carouselRef: ReturnType<typeof useEmblaCarousel>[0]
  api: ReturnType<typeof useEmblaCarousel>[1]
  scrollPrev: () => void
  scrollNext: () => void
  canScrollPrev: boolean
  canScrollNext: boolean
} & CarouselProps

const CarouselContext = React.createContext<CarouselContextProps | null>(null)

function useCarousel() {
  const context = React.useContext(CarouselContext)

  if (!context) {
    throw new Error("useCarousel must be used within a <Carousel />")
  }

  return context
}

const Carousel = React.forwardRef<
  HTMLDivElement,
  React.HTMLAttributes<HTMLDivElement> & CarouselProps
>(
  (
    {
      orientation = "horizontal",
      opts,
      setApi,
      plugins,
      className,
      children,
      ...props
    },
    ref
  ) => {
    const [carouselRef, api] = useEmblaCarousel(
      {
        ...opts,
        axis: orientation === "horizontal" ? "x" : "y",
      },
      plugins
    )
    const [canScrollPrev, setCanScrollPrev] = React.useState(false)
    const [canScrollNext, setCanScrollNext] = React.useState(false)

    const onSelect = React.useCallback((api: CarouselApi) => {
      if (!api) {
        return
      }

      setCanScrollPrev(api.canScrollPrev())
      setCanScrollNext(api.canScrollNext())
    }, [])

    const scrollPrev = React.useCallback(() => {
      api?.scrollPrev()
    }, [api])

    const scrollNext = React.useCallback(() => {
      api?.scrollNext()
    }, [api])

    const handleKeyDown = React.useCallback(
      (event: React.KeyboardEvent<HTMLDivElement>) => {
        if (event.key === "ArrowLeft") {
          event.preventDefault()
          scrollPrev()
        } else if (event.key === "ArrowRight") {
          event.preventDefault()
          scrollNext()
        }
      },
      [scrollPrev, scrollNext]
    )

    React.useEffect(() => {
      if (!api || !setApi) {
        return
      }

      setApi(api)
    }, [api, setApi])

    React.useEffect(() => {
      if (!api) {
        return
      }

      onSelect(api)
      api.on("reInit", onSelect)
      api.on("select", onSelect)

      return () => {
        api?.off("select", onSelect)
      }
    }, [api, onSelect])

    return (
      <CarouselContext.Provider
        value={{
          carouselRef,
          api: api,
          opts,
          orientation:
            orientation || (opts?.axis === "y" ? "vertical" : "horizontal"),
          scrollPrev,
          scrollNext,
          canScrollPrev,
          canScrollNext,
        }}
      >
        <div
          ref={ref}
          onKeyDownCapture={handleKeyDown}
          className={cn("relative", className)}
          role="region"
          aria-roledescription="carousel"
          {...props}
        >
          {children}
        </div>
      </CarouselContext.Provider>
    )
  }
)
Carousel.displayName = "Carousel"

const CarouselContent = React.forwardRef<
  HTMLDivElement,
  React.HTMLAttributes<HTMLDivElement>
>(({ className, ...props }, ref) => {
  const { carouselRef, orientation } = useCarousel()

  return (
    <div ref={carouselRef} className="overflow-hidden">
      <div
        ref={ref}
        className={cn(
          "flex",
          orientation === "horizontal" ? "-ml-4" : "-mt-4 flex-col",
          className
        )}
        {...props}
      />
    </div>
  )
})
CarouselContent.displayName = "CarouselContent"

const CarouselItem = React.forwardRef<
  HTMLDivElement,
  React.HTMLAttributes<HTMLDivElement>
>(({ className, ...props }, ref) => {
  const { orientation } = useCarousel()

  return (
    <div
      ref={ref}
      role="group"
      aria-roledescription="slide"
      className={cn(
        "min-w-0 shrink-0 grow-0 basis-full",
        orientation === "horizontal" ? "pl-4" : "pt-4",
        className
      )}
      {...props}
    />
  )
})
CarouselItem.displayName = "CarouselItem"

const CarouselPrevious = React.forwardRef<
  HTMLButtonElement,
  React.ComponentProps<typeof Button>
>(({ className, variant = "outline", size = "icon", ...props }, ref) => {
  const { orientation, scrollPrev, canScrollPrev } = useCarousel()

  return (
    <Button
      ref={ref}
      variant={variant}
      size={size}
      className={cn(
        "absolute  h-8 w-8 rounded-full",
        orientation === "horizontal"
          ? "-left-12 top-1/2 -translate-y-1/2"
          : "-top-12 left-1/2 -translate-x-1/2 rotate-90",
        className
      )}
      disabled={!canScrollPrev}
      onClick={scrollPrev}
      {...props}
    >
      <ArrowLeft className="h-4 w-4" />
      <span className="sr-only">Previous slide</span>
    </Button>
  )
})
CarouselPrevious.displayName = "CarouselPrevious"

const CarouselNext = React.forwardRef<
  HTMLButtonElement,
  React.ComponentProps<typeof Button>
>(({ className, variant = "outline", size = "icon", ...props }, ref) => {
  const { orientation, scrollNext, canScrollNext } = useCarousel()

  return (
    <Button
      ref={ref}
      variant={variant}
      size={size}
      className={cn(
        "absolute h-8 w-8 rounded-full",
        orientation === "horizontal"
          ? "-right-12 top-1/2 -translate-y-1/2"
          : "-bottom-12 left-1/2 -translate-x-1/2 rotate-90",
        className
      )}
      disabled={!canScrollNext}
      onClick={scrollNext}
      {...props}
    >
      <ArrowRight className="h-4 w-4" />
      <span className="sr-only">Next slide</span>
    </Button>
  )
})
CarouselNext.displayName = "CarouselNext"

export {
  type CarouselApi,
  Carousel,
  CarouselContent,
  CarouselItem,
  CarouselPrevious,
  CarouselNext,
}

