@layer base {
  * {
    @apply border-border;
  }

  body {
    @apply font-sans antialiased bg-background text-foreground;
  }
}

@layer utilities {

  /* GPU acceleration for smooth animations */
  .transform-gpu {
    transform: translate3d(0, 0, 0);
    will-change: transform;
  }

  /* Hide ugly search cancel button in Chrome until we can style it properly */
  input[type="search"]::-webkit-search-cancel-button {
    @apply hidden;
  }

  /* Placeholder styling for contentEditable div */
  [contenteditable][data-placeholder]:empty::before {
    content: attr(data-placeholder);
    color: hsl(var(--muted-foreground));
    pointer-events: none;
  }

  .no-default-hover-elevate {}

  .no-default-active-elevate {}

  .toggle-elevate::before,
  .toggle-elevate-2::before {
    content: "";
    pointer-events: none;
    position: absolute;
    inset: 0px;
    border-radius: inherit;
    z-index: -1;
  }

  .toggle-elevate.toggle-elevated::before {
    background-color: var(--elevate-2);
  }

  .border.toggle-elevate::before {
    inset: -1px;
  }

  .hover-elevate:not(.no-default-hover-elevate),
  .active-elevate:not(.no-default-active-elevate),
  .hover-elevate-2:not(.no-default-hover-elevate),
  .active-elevate-2:not(.no-default-active-elevate) {
    position: relative;
    z-index: 0;
  }

  .hover-elevate:not(.no-default-hover-elevate)::after,
  .active-elevate:not(.no-default-active-elevate)::after,
  .hover-elevate-2:not(.no-default-hover-elevate)::after,
  .active-elevate-2:not(.no-default-active-elevate)::after {
    content: "";
    pointer-events: none;
    position: absolute;
    inset: 0px;
    border-radius: inherit;
    z-index: 999;
  }

  .hover-elevate:hover:not(.no-default-hover-elevate)::after,
  .active-elevate:active:not(.no-default-active-elevate)::after {
    background-color: var(--elevate-1);
  }

  .hover-elevate-2:hover:not(.no-default-hover-elevate)::after,
  .active-elevate-2:active:not(.no-default-active-elevate)::after {
    background-color: var(--elevate-2);
  }

  .border.hover-elevate:not(.no-hover-interaction-elevate)::after,
  .border.active-elevate:not(.no-active-interaction-elevate)::after,
  .border.hover-elevate-2:not(.no-hover-interaction-elevate)::after,
  .border.active-elevate-2:not(.no-active-interaction-elevate)::after,
  .border.hover-elevate:not(.no-hover-interaction-elevate)::after {
    inset: -1px;
  }

  @keyframes liquid-wave-1 {
    0% { transform: translate3d(0, 0, 0); }
    100% { transform: translate3d(-100px, 0, 0); }
  }

  @keyframes fade-in {
    from { opacity: 0; }
    to { opacity: 1; }
  }

  @keyframes float {
    0%, 100% { transform: translateY(0px); }
    50% { transform: translateY(-10px); }
  }

  @keyframes pulse-scale {
    0%, 100% { transform: scale(1); }
    50% { transform: scale(1.05); }
  }

  @keyframes shimmer {
    0% { background-position: -200% center; }
    100% { background-position: 200% center; }
  }

  @keyframes bounce-subtle {
    0%, 100% { transform: translateY(0); }
    50% { transform: translateY(-5px); }
  }

  @keyframes glow {
    0%, 100% { box-shadow: 0 0 20px rgba(16, 185, 129, 0.3); }
    50% { box-shadow: 0 0 30px rgba(16, 185, 129, 0.6); }
  }

  .animate-liquid-wave-1 {
    animation: liquid-wave-1 4s linear infinite;
  }

  .animate-fade-in {
    animation: fade-in 0.3s ease-in;
  }

  .animate-float {
    animation: float 3s ease-in-out infinite;
  }

  .animate-pulse-scale {
    animation: pulse-scale 2s ease-in-out infinite;
  }

  .animate-shimmer {
    background: linear-gradient(90deg, transparent, rgba(255,255,255,0.1), transparent);
    background-size: 200% 100%;
    animation: shimmer 2s linear infinite;
  }

  .animate-bounce-subtle {
    animation: bounce-subtle 2s ease-in-out infinite;
  }

  .animate-glow {
    animation: glow 2s ease-in-out infinite;
  }

  .glass {
    background: rgba(255, 255, 255, 0.7);
    backdrop-filter: blur(20px);
    -webkit-backdrop-filter: blur(20px);
    border: 1px solid rgba(255, 255, 255, 0.3);
  }

  .glass-dark {
    background: rgba(30, 41, 59, 0.6);
    backdrop-filter: blur(20px);
    -webkit-backdrop-filter: blur(20px);
    border: 1px solid rgba(255, 255, 255, 0.1);
  }

  .glass-card {
    background: rgba(255, 255, 255, 0.85);
    backdrop-filter: blur(16px);
    -webkit-backdrop-filter: blur(16px);
    border: 1px solid rgba(255, 255, 255, 0.4);
    box-shadow: 0 8px 32px 0 rgba(16, 185, 129, 0.1);
  }

  .glass-card-dark {
    background: rgba(30, 41, 59, 0.7);
    backdrop-filter: blur(16px);
    -webkit-backdrop-filter: blur(16px);
    border: 1px solid rgba(255, 255, 255, 0.15);
    box-shadow: 0 8px 32px 0 rgba(0, 0, 0, 0.3);
  }

  .glass-modal {
    background: rgba(255, 255, 255, 0.95);
    backdrop-filter: blur(30px);
    -webkit-backdrop-filter: blur(30px);
    border: 1px solid rgba(255, 255, 255, 0.5);
    box-shadow: 0 20px 60px 0 rgba(16, 185, 129, 0.15);
  }

  .glass-modal-dark {
    background: rgba(30, 41, 59, 0.85);
    backdrop-filter: blur(30px);
    -webkit-backdrop-filter: blur(30px);
    border: 1px solid rgba(255, 255, 255, 0.2);
    box-shadow: 0 20px 60px 0 rgba(0, 0, 0, 0.5);
  }

  @keyframes particle-float {
    0%, 100% { 
      transform: translate(0, 0) scale(1);
      opacity: 0.3;
    }
    25% { 
      transform: translate(10px, -10px) scale(1.1);
      opacity: 0.5;
    }
    50% { 
      transform: translate(-5px, -20px) scale(0.9);
      opacity: 0.4;
    }
    75% { 
      transform: translate(-15px, -10px) scale(1.05);
      opacity: 0.6;
    }
  }

  .animate-particle-float {
    animation: particle-float 8s ease-in-out infinite;
  }

  @keyframes smooth-pulse {
    0%, 100% { 
      transform: scale(1);
      opacity: 1;
    }
    50% { 
      transform: scale(1.03);
      opacity: 0.95;
    }
  }

  .animate-smooth-pulse {
    animation: smooth-pulse 3s ease-in-out infinite;
  }

  /* Liquid blob animations - optimized for 120fps with GPU acceleration */
  @keyframes liquid-float-1 {
    0%, 100% { 
      transform: translate3d(0, 0, 0) scale3d(1, 1, 1);
      border-radius: 60% 40% 30% 70% / 60% 30% 70% 40%;
    }
    25% { 
      transform: translate3d(100px, -80px, 0) scale3d(1.1, 0.9, 1);
      border-radius: 30% 60% 70% 40% / 50% 60% 30% 60%;
    }
    50% { 
      transform: translate3d(-60px, 100px, 0) scale3d(0.9, 1.1, 1);
      border-radius: 50% 60% 30% 60% / 30% 60% 70% 40%;
    }
    75% { 
      transform: translate3d(80px, 60px, 0) scale3d(1.05, 0.95, 1);
      border-radius: 60% 40% 60% 40% / 40% 60% 40% 60%;
    }
  }

  @keyframes liquid-float-2 {
    0%, 100% { 
      transform: translate3d(0, 0, 0) scale3d(1, 1, 1);
      border-radius: 40% 60% 60% 40% / 40% 60% 40% 60%;
    }
    33% { 
      transform: translate3d(-120px, 90px, 0) scale3d(0.95, 1.05, 1);
      border-radius: 60% 40% 30% 70% / 60% 30% 70% 40%;
    }
    66% { 
      transform: translate3d(90px, -70px, 0) scale3d(1.08, 0.92, 1);
      border-radius: 30% 60% 70% 40% / 50% 40% 60% 50%;
    }
  }

  @keyframes liquid-float-3 {
    0%, 100% { 
      transform: translate3d(0, 0, 0) scale3d(1, 1, 1);
      border-radius: 50% 50% 40% 60% / 50% 50% 60% 40%;
    }
    20% { 
      transform: translate3d(70px, 110px, 0) scale3d(0.92, 1.08, 1);
      border-radius: 60% 40% 50% 50% / 40% 60% 50% 50%;
    }
    40% { 
      transform: translate3d(-90px, -60px, 0) scale3d(1.06, 0.94, 1);
      border-radius: 40% 60% 60% 40% / 60% 40% 40% 60%;
    }
    60% { 
      transform: translate3d(50px, -90px, 0) scale3d(0.98, 1.02, 1);
      border-radius: 50% 50% 50% 50% / 60% 40% 60% 40%;
    }
    80% { 
      transform: translate3d(-70px, 80px, 0) scale3d(1.03, 0.97, 1);
      border-radius: 55% 45% 45% 55% / 45% 55% 55% 45%;
    }
  }

  .liquid-blob {
    position: absolute;
    background: radial-gradient(circle, rgba(16, 185, 129, 0.15) 0%, rgba(16, 185, 129, 0.05) 50%, transparent 100%);
    filter: blur(40px);
    will-change: transform, border-radius;
    transform: translate3d(0, 0, 0);
  }

  .liquid-blob-1 {
    width: 500px;
    height: 500px;
    left: 10%;
    top: 20%;
    animation: liquid-float-1 25s ease-in-out infinite;
  }

  .liquid-blob-2 {
    width: 600px;
    height: 600px;
    right: 15%;
    top: 10%;
    animation: liquid-float-2 30s ease-in-out infinite;
    animation-delay: -5s;
  }

  .liquid-blob-3 {
    width: 450px;
    height: 450px;
    left: 50%;
    bottom: 15%;
    animation: liquid-float-3 28s ease-in-out infinite;
    animation-delay: -10s;
  }

  .liquid-blob-4 {
    width: 550px;
    height: 550px;
    right: 30%;
    bottom: 20%;
    animation: liquid-float-1 32s ease-in-out infinite;
    animation-delay: -15s;
  }

  .liquid-blob-5 {
    width: 480px;
    height: 480px;
    left: 30%;
    top: 50%;
    animation: liquid-float-2 27s ease-in-out infinite;
    animation-delay: -8s;
  }

  /* SVG liquid circles animation */
  @keyframes liquid-circle-1 {
    0%, 100% { 
      cx: 20%; 
      cy: 30%;
      transform: scale3d(1, 1, 1);
    }
    33% { 
      cx: 35%; 
      cy: 60%;
      transform: scale3d(1.2, 0.8, 1);
    }
    66% { 
      cx: 15%; 
      cy: 70%;
      transform: scale3d(0.9, 1.1, 1);
    }
  }

  @keyframes liquid-circle-2 {
    0%, 100% { 
      cx: 70%; 
      cy: 25%;
      transform: scale3d(1, 1, 1);
    }
    50% { 
      cx: 80%; 
      cy: 65%;
      transform: scale3d(0.85, 1.15, 1);
    }
  }

  @keyframes liquid-circle-3 {
    0%, 100% { 
      cx: 50%; 
      cy: 70%;
      transform: scale3d(1, 1, 1);
    }
    25% { 
      cx: 30%; 
      cy: 40%;
      transform: scale3d(1.1, 0.9, 1);
    }
    75% { 
      cx: 65%; 
      cy: 50%;
      transform: scale3d(0.95, 1.05, 1);
    }
  }

  @keyframes liquid-circle-4 {
    0%, 100% { 
      cx: 85%; 
      cy: 80%;
      transform: scale3d(1, 1, 1);
    }
    40% { 
      cx: 60%; 
      cy: 20%;
      transform: scale3d(0.9, 1.1, 1);
    }
    80% { 
      cx: 40%; 
      cy: 75%;
      transform: scale3d(1.05, 0.95, 1);
    }
  }

  .liquid-circle-1 {
    animation: liquid-circle-1 20s ease-in-out infinite;
    will-change: transform;
    transform-origin: center;
  }

  .liquid-circle-2 {
    animation: liquid-circle-2 25s ease-in-out infinite;
    will-change: transform;
    transform-origin: center;
  }

  .liquid-circle-3 {
    animation: liquid-circle-3 22s ease-in-out infinite;
    will-change: transform;
    transform-origin: center;
  }

  .liquid-circle-4 {
    animation: liquid-circle-4 23s ease-in-out infinite;
    will-change: transform;
    transform-origin: center;
  }
}

